const {
  time,
  unit,
} = msg;

const unitCoefficientMap = {
  ms: 1,
  s: 1000,
  min: 1000 * 60,
  h: 1000 * 60 * 60,
};

if (time <= 0) {
  return msg;
}

const timeToWait = time * unitCoefficientMap[unit];

if (supersonic.api.delay) {
  supersonic.api.delay(() => { node.send(msg); }, timeToWait, msg.contextId);
} else {
  setTimeout(() => {
    node.send(msg);
  }, timeToWait);
}

