const {
  page,
  params,
  openInModal,
  supersonic,
} = inputs;
const obj = {
  route: page,
  params: params || {},
};
const action = openInModal ? 'pushToModal' : 'pushToStack';
await supersonic.api.navigation[action](obj);

const {
  url,
  method,
  headers,
  body,
  bodyType,
} = inputs;

let stringifiedBody = null;

if (method !== 'GET' && body) {
  try {
    if (bodyType === 'json') {
      stringifiedBody = JSON.stringify(body);
    } else if (bodyType === 'x-www-form-urlencoded') {
      const paramsArray = Object.keys(body).map((key) => `${encodeURIComponent(key)}=${encodeURIComponent(body[key])}`);
      stringifiedBody = paramsArray.join('&');
    } else {
      return [1, {
        error: {
          code: 'unknownBodyType',
          message: `Unknown body type: ${bodyType}`,
          rawError: null,
        },
      }];
    }
  } catch (error) {
    return [1, {
      error: {
        code: 'bodyNotStringifiable',
        message: 'Could not stringify/process the request body',
        rawError: error,
      },
    }];
  }
}
const postRequest = new Request(url);
const reqHeaders = new Headers();

if (Array.isArray(headers)) {
  headers
    .filter(({ header, value }) => typeof header === 'string' && typeof value === 'string' && header.length && value.length)
    .forEach(({ header, value }) => reqHeaders.append(header, value));
}

if (bodyType === 'json') {
  reqHeaders.append('Content-Type', 'application/json');
}

if (bodyType === 'x-www-form-urlencoded') {
  reqHeaders.append('Content-Type', 'application/x-www-form-urlencoded');
}

reqHeaders.append('Accept', 'application/json');

const init = {
  method,
  headers: reqHeaders,
  mode: 'cors',
  body: stringifiedBody,
};

try {
  const response = await fetch(postRequest, init);
  let resBodyParsed;
  let resBodyRaw;
  if (response) {
    try {
      resBodyParsed = await response.json();
      resBodyRaw = JSON.stringify(resBodyParsed);
    } catch (e) {
      resBodyParsed = null;
      resBodyRaw = null;
    }

    if (!response.ok && ((response.status >= 400 && response.status <= 451) || (response.status >= 500 && response.status <= 524))) {
      return [1, {
        statusCode: response.status,
        statusText: response.statusText,
        resHeaders: response.headers,
        resBodyRaw,
        resBodyParsed,
        resBody: resBodyParsed,
      }];
    }

    if (!response.ok) { throw response; }
    return [0, {
      statusCode: response.status,
      statusText: response.statusText,
      resHeaders: response.headers,
      resBodyRaw,
      resBodyParsed,
      resBody: resBodyParsed,
    }];
  }
} catch (error) {
  let errorStr = String(error);
  if (errorStr === 'TypeError: Failed to fetch') {
    errorStr += '. Does the server allow CORS?';
    return [2, {
      error: {
        code: 'requestFailed',
        status: error.status,
        message: errorStr,
        rawError: error,
      },
    }];
  }
  if (error.status) {
    return [2, {
      error: {
        code: 'httpError',
        message: 'Error was thrown because the response\'s status code was not 2XX, 4XX, 5XX or valid one within that range. ',
        rawError: error,
      },
    }];
  }
  return [2, {
    error: {
      code: 'unknown',
      message: `Unknown error occurred while making the ${method} request to ${url}.`,
      rawError: error,
    },
  }];
}

const { request, supersonic } = inputs;
const { resourceName, id, parameters = {} } = request;

async function makeRequest() {
  const resourceConfig = supersonic.api.data.getResourceConfig(resourceName);
  if (!resourceConfig) {
    // This is assumed to be a legacy data resource
    const data = await supersonic.resource.find(resourceName, id, {});
    return data && data.value && data.value.object;
  }
  // Load a local data resource
  if (resourceConfig.type === 'local') {
    const { appId } = await supersonic.api.app.getAppId();
    const key = `local-resource:${appId}:${resourceName}:${id}`;
    const { data: record } = await supersonic.api.asyncStorage.getItem({ key });
    if (record != null && typeof record === 'object' && typeof record.id !== 'undefined') {
      return record;
    }
    throw new Error(`Record ${resourceName} with ID ${id} not found`);
  }
  if (resourceConfig.type === 'agdb') {
    const data = await supersonic.resource.find(resourceConfig.agdb_id, id, {});
    return data && data.value && data.value.object;
  }
  // Load a "REST" data resource
  const operation = resourceConfig.operations.retrieve;
  if (!operation) {
    throw new Error(`Resource ${resourceName} does not support getting a single record`);
  }
  const requestParameters = [
    ...resourceConfig.parameters,
    ...operation.parameters,
  ];
  const headers = {};
  const queryComponents = [];
  let url = `${resourceConfig.base_url}${operation.relative_path || ''}`;
  requestParameters.forEach((parameter) => {
    let value = parameters[parameter.id];
    if (parameter.is_static || typeof value === 'undefined') {
      value = parameter.default;
    }
    if (value == null) {
    // No value for this parameter
      if (parameter.is_optional) {
        return;
      }
      throw new Error(`Missing required parameter ${JSON.stringify(parameter.title)}`);
    }
    // Map the parameter to headers/URL/query
    const { placement } = parameter;
    const placementKey = parameter.placement_key;
    if (placement === 'header') {
      // Add to headers
      headers[placementKey] = value;
    } else if (placement === 'query') {
      // Add to the URL as a query parameter
      queryComponents.push(`${encodeURIComponent(placementKey)}=${encodeURIComponent(String(value))}`);
    } else if (placement === 'path') {
      // Replace the placeholder in the URL with escaped string
      url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), encodeURIComponent(String(value)));
    } else if (placement === 'url') {
      // Replace the placeholder in the URL with unescaped string
      url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), String(value));
    }
  });
  // Add any query parameters to the URL
  if (queryComponents.length) {
    const newQuery = queryComponents.join('&');
    url = url.replace(/(?:\?(.*))?$/, (_, query) => (query ? `?${query}&${newQuery}` : `?${newQuery}`));
  }
  // Perform the request
  const response = await fetch(url, { method: 'GET', headers });
  if (!response.ok) {
    throw response;
  }
  let result = await response.json();
  const resultPath = operation.response_key_path ? operation.response_key_path.split('.') : [];
  while (result != null && resultPath.length) {
    result = result[resultPath.shift()];
  }
  if (!result) {
    throw new Error(`A ${resourceName} record not found from the response!`);
  }
  return result;
}

try {
  const record = await makeRequest();
  if (Array.isArray(record)) {
    const errorStr = 'The server responded with a list for a single data record. The response must be an object.';
    return [1, {
      error: {
        code: 'responseMustBeObject',
        status: -1,
        message: errorStr,
        rawError: new Error(errorStr),
      },
    }];
  }
  return [0, { record }];
} catch (error) {
  // eslint-disable-next-line no-console
